// import React from "react";
import React, { useRef, useState } from "react";
import "./Skybox"
import { Canvas, useFrame } from "@react-three/fiber";

import SkyBox from "./Skybox";
import "./Skybox.scss"
import "./App.css"
import { OrbitControls, PerspectiveCamera} from '@react-three/drei'
import { DoubleSide, Mesh } from "three";
import { Sky } from "@react-three/drei";
function Box(props:any) {
  const mesh = useRef<Mesh>(null!);

  // mesh.current.rotation.x += Math.PI / 2;
  // useFrame((state, delta) => (mesh.current.rotation.z += delta))

  return (
     <mesh castShadow receiveShadow {...props} ref={mesh} scale={0.5} rotation={[0,Math.PI / 4,0]} position={[0,,0]}>
        <boxGeometry args={[0.8, 0.1, 0.1]} />
        <meshBasicMaterial attach="material" color={"red"} />
     </mesh>
  );
}

function Plane(props:any) {
  const mesh = useRef<Mesh>(null!);

  // mesh.current.rotation.x += Math.PI / 2;
  // useFrame((state, delta) => (mesh.current.rotation.z += delta))

  return (
    <mesh castShadow receiveShadow {...props} ref={mesh} rotation={[Math.PI / 2, 0, Math.PI / 4]} scale={[3, 3, 3]}>
      <planeGeometry args={[1, 1, 1]}/>
      <meshBasicMaterial color="grey" side={DoubleSide} />
    </mesh>
  );
}

export default function App(props:any) {
  const boxRef = useRef();
   return (
      // <div> 
        <Canvas shadows className="skybox" camera={{ position: [0, 0, 1], fov: 60 }}>
          {/* <ambientLight /> */}
          <ambientLight intensity={0.1} />
          <directionalLight
            intensity={0.5}
            castShadow
            shadow-mapSize-height={512}
            shadow-mapSize-width={512}
            shadow-camera-left={-20}
            position={[80, 80, 30]}
          />
          <SkyBox /> 

          <Plane />
          <Box />
          {/* <group>
          <Box castShadow receiveShadow ref={boxRef} position={[0, 0, 0]}>
            <meshStandardMaterial attach="material" color="white" />
          </Box>
          <Plane
            receiveShadow
            rotation={[-Math.PI / 2, 0, 0]}
            position={[0, -1, 0]}
            args={[1000, 1000]}
          >
            <meshStandardMaterial attach="material" color="white" />
          </Plane>
        </group> */}
          <PerspectiveCamera position={[2, 2, 2]} makeDefault />
          <OrbitControls />

        </Canvas>
   );
}
