// import React from "react";
import React, { useRef } from "react";
import "./Skybox"
import { Canvas, useFrame, use } from "@react-three/fiber";
import SkyBox from "./Skybox";
import "./Skybox.scss"
import "./App.css"
import { OrbitControls, PerspectiveCamera } from '@react-three/drei'
import { DoubleSide } from "three";
// import { Sky } from "@react-three/drei";
function Box({ position }) {
  // Register box as a physics body with mass
  const ref = useCannon({ mass: 100000 }, body => {
   body.addShape(new CANNON.Box(new CANNON.Vec3(1, 1, 1)))
   body.position.set(...position)
  })
  return (
   <mesh ref={ref} castShadow receiveShadow>
    <boxGeometry attach="geometry" args={[2, 2, 2]} />
    <meshStandardMaterial attach="material" />
   </mesh>
  )
 }
export default function App(props:any) {

   return (
      // <div> 
        <Canvas className="skybox" camera={{ position: [0, 0, 3] }}>
          <ambientLight />
          <mesh position={[0, 0, 0]} rotation={[Math.PI / 2, 0, Math.PI / 4]} scale={[3, 3, 3]}>
            <planeBufferGeometry />
            <meshBasicMaterial color="grey" side={DoubleSide} />
          </mesh>
          <SkyBox /> 
          <Box position={[0, 0, 0]} />
          <PerspectiveCamera position={[2, 2, 2]} makeDefault />
          <OrbitControls />

        </Canvas>
   );
}
