// import React from "react";
import React, { useRef, useState } from "react";
import "./Skybox"
import { Canvas, useFrame } from "@react-three/fiber";
import SkyBox from "./Skybox";
import "./Skybox.scss"
import "./App.css"
import * as THREE from 'three'
import { Stats, OrbitControls, PerspectiveCamera } from '@react-three/drei'
import { DoubleSide } from "three";
// import { Sky } from "@react-three/drei";
function Box(props:Any) {
  const mesh = useRef();
  useFrame(() => (mesh.current.rotation.x = mesh.current.rotation.y += 0.01));
  return (
     <mesh {...props} ref={mesh}>
        <boxGeometry args={[3, 3, 3]} />
        <meshStandardMaterial color={"orange"} />
     </mesh>
  );
}
export default function App(props:any) {

   return (
      // <div> 
        <Canvas className="skybox" camera={{ position: [0, 0, 3] }}>
          <ambientLight />
          <mesh position={[0, 0, 0]} rotation={[Math.PI / 2, 0, Math.PI / 4]} scale={[3, 3, 3]}>
            <planeBufferGeometry />
            <meshBasicMaterial color="grey" side={DoubleSide} />
          </mesh>
          <SkyBox /> 
          <PerspectiveCamera position={[2, 2, 2]} makeDefault />
          <OrbitControls />

        </Canvas>
   );
}
