// import React from "react";
import React, { useRef } from "react";
import "./Skybox"
import { Canvas, useFrame } from "@react-three/fiber";
import SkyBox from "./Skybox";
import "./Skybox.scss"
import "./App.css"
import { OrbitControls, PerspectiveCamera } from '@react-three/drei'
import { DoubleSide } from "three";
// import { Sky } from "@react-three/drei";
function Box() {
const box = useRef()
 useFrame(() => box.current && void (box.current.rotation.x += 0.01, box.current.rotation.y += 0.01))
 return (
  <mesh ref={box}>
   <boxGeometry attach="geometry" args={[0.01, 0.01, 0.01]} />
   <meshStandardMaterial attach="material" color={0x0ff000} />
  </mesh>
 );
}
export default function App(props:any) {

   return (
      // <div> 
        <Canvas className="skybox" camera={{ position: [0, 0, 3] }}>
          <ambientLight />
          <mesh position={[0, 0, 0]} rotation={[Math.PI / 2, 0, Math.PI / 4]} scale={[3, 3, 3]}>
            <planeBufferGeometry />
            <meshBasicMaterial color="grey" side={DoubleSide} />
          </mesh>
          <SkyBox /> 
          <Box position={[0, 0, 0]} />
          <PerspectiveCamera position={[2, 2, 2]} makeDefault />
          <OrbitControls />

        </Canvas>
   );
}
